<?phpnamespace FactionsPro\tasks;use FactionsPro\FactionMain;use pocketmine\Server;use pocketmine\scheduler\Task;use pocketmine\Player;use pocketmine\utils\TextFormat;class FactionDeleteTask extends Task{		public $plugin;		public function __construct(FactionMain $plugin){		$this->plugin = $plugin;	}	public function onRun(int $currentTick) : void{foreach($this->plugin->getServer()->getOnlinePlayers() as $player){	if($this->plugin->isInFaction($player->getName()) == false){		if($this->plugin->prefs->get("enable-faction-tag") == true){		$player->setNameTagVisible(true);		$player->setNameTag($player->getName());		$player->setDisplayName($player->getName());		$player->setScoreTag($player->getName());	}}}}}